import { getStrapiURL } from "./api";

type Props = {
  url: string
};

// type imageUrl = {
//   image: string
// }

export function getStrapiMedia(media: Props): string {
  console.log(media)
  const imageUrl = media.url.startsWith("/")
    ? getStrapiURL(media.url)
    : media.url;
  return imageUrl;
}
